// This is the "root" _module_ which is loaded by js/index.js and imports all others.

import init, { start_game } from './all_is_cubes_wasm.js';  // Rust WASM module is named this for some reason
import { makeAllStaticGuiHelpers } from './gui.js';

export async function bootstrap() {
  // init() is a function generated by wasm-pack (wasm-bindgen?) which loads the actual wasm
  await init();
  start_game(makeAllStaticGuiHelpers(window, document));
}